import React ,{useState}from "react";
import AddIcon from '@mui/icons-material/Add';
import Button from '@mui/material/Button';
import './App.css';
const App=()=>{
const [state, setstate] = useState(0);

const add=()=>{
  setstate(state+1);
}

const sub=()=>{
  if(state!==0)
  {setstate(state-1);
  }
  else{
    alert("can not go lessthen 0");
  }
}
return(
  <div className="maindiv">
    <span className="txt">{state}</span>
    <Button className="btn" onClick={add}><AddIcon/></Button>
    <Button className="btn" onClick={sub}>-</Button>
  </div>
)
;
}
export default App;
